策略1{'statement":["Action":['ros:DescribeStacks'].ros:DescribeStackDetail},"Effect":"Allow","Resource":"acs:ros:cn-beijing:*stack/*"] ."Version":"1号};
策略2{"statement":K"Action":['ros:Create stack","ros:Describe Stacks","ros:Describe Stack Detai","ros:Validate Template"],"Effect":"Allow","Resource"'"*"},"Version":"1"};策略3{"Statement":{"Action":{'ros:Update Stack"},"Effect":"Allow","Resource":*acs:ros:cn-bejjing:123456789:stack/mystack/94dd5431-2df6-4415-81ca-732a7082252a] ],"Version":"1};策略4"Statement":["Action":{'ros:Describe Stack Detai" "ros:Abandon Stack},"Effect":"Allow","Resource":"acs:ros:cn-beijing:123456789:stack/mystack/94dd5431-2df6-4415-81ca-732a7082252a]]."Version":"1}
